import React from 'react';
import './Widgets.css';

const WeatherWidget = ({ data }) => {
  // Si no hay datos, mostrar un mensaje de carga
  if (!data) {
    return (
      <div className="widget weather-widget">
        <h2>Clima Actual</h2>
        <div className="widget-content loading">
          <p>Cargando datos meteorol√≥gicos...</p>
        </div>
      </div>
    );
  }

  // Datos de ejemplo (en una implementaci√≥n real, estos vendr√≠an de la prop data)
  const weatherData = data || {
    location: 'Ciudad de M√©xico',
    temperature: 23,
    humidity: 65,
    windSpeed: 10,
    windDirection: 'NE',
    conditions: 'Parcialmente nublado',
    icon: 'partly-cloudy'
  };

  // Funci√≥n para determinar el √≠cono basado en las condiciones
  const getWeatherIcon = (condition) => {
    const conditionMap = {
      'soleado': '‚òÄÔ∏è',
      'parcialmente nublado': '‚õÖ',
      'nublado': '‚òÅÔ∏è',
      'lluvia': 'üåßÔ∏è',
      'tormenta': '‚õàÔ∏è',
      'nieve': '‚ùÑÔ∏è',
      'niebla': 'üå´Ô∏è'
    };

    // Convertir a min√∫sculas para la comparaci√≥n
    const conditionLower = condition.toLowerCase();
    return conditionMap[conditionLower] || 'üå°Ô∏è';
  };

  return (
    <div className="widget weather-widget">
      <h2>Clima Actual</h2>
      <div className="widget-content">
        <div className="weather-location">
          <span>{weatherData.location}</span>
        </div>
        
        <div className="weather-main">
          <div className="weather-icon">
            {getWeatherIcon(weatherData.conditions)}
          </div>
          <div className="weather-temp">
            <span className="temp-value">{weatherData.temperature}¬∞C</span>
            <span className="temp-condition">{weatherData.conditions}</span>
          </div>
        </div>
        
        <div className="weather-details">
          <div className="weather-detail">
            <span className="detail-label">Humedad:</span>
            <span className="detail-value">{weatherData.humidity}%</span>
          </div>
          <div className="weather-detail">
            <span className="detail-label">Viento:</span>
            <span className="detail-value">{weatherData.windSpeed} km/h {weatherData.windDirection}</span>
          </div>
        </div>
        
        <div className="widget-footer">
          <a href="/weather-monitoring" className="widget-link">Ver detalles completos</a>
        </div>
      </div>
    </div>
  );
};

export default WeatherWidget;
